CREATE OR REPLACE FORCE VIEW sbxtax.tbv_zone_aliases (zone_match_pattern_id,zone_match_context_id,merchant_id,"PATTERN","VALUE","TYPE",zone_level_id,zone_id,created_by,creation_date,last_updated_by,last_update_date) AS
select 				p.ZONE_MATCH_PATTERN_ID, 				c.ZONE_MATCH_CONTEXT_ID, 				p.MERCHANT_ID, 				p.PATTERN, 				p.VALUE, 				p.TYPE, 				c.ZONE_LEVEL_ID, 				c.ZONE_ID, 				c.CREATED_BY, 				c.CREATION_DATE, 				c.LAST_UPDATED_BY, 				c.LAST_UPDATE_DATE 			 from TB_ZONE_MATCH_PATTERNS p 			      ,TB_ZONE_MATCH_CONTEXTS c 			 where p.ZONE_MATCH_PATTERN_ID = c.ZONE_MATCH_PATTERN_ID;